import React from 'react'
import { Link, useLocation } from 'react-router-dom'

export default function Sidebar() {
    const location = useLocation() // Get the current location object

    const menu = [
        {
            url: '/profile/orders',
            name: 'Orders',
        },
        {
            url: '/profile/favourites',
            name: 'Favourites',
        },
        {
            url: '/profile/payments',
            name: 'Payments',
        },
        {
            url: '/profile/addresses',
            name: 'Addresses',
        },
        {
            url: '/profile/settings',
            name: 'Settings',
        },
    ]

    return (
        <div className='sidebar'>
            {menu.map((item, index) => {
                return (
                    <Link
                        key={index}
                        to={item.url}
                        className={`sidebar-item ${location.pathname === item.url ? 'bg-white' : ''}`} // Use location.pathname for dynamic matching
                    >
                        {item.name}
                    </Link>
                )
            })}
        </div>
    )
}
