import React, { useEffect, useState } from 'react'
import { Card, Row, Col, Carousel } from 'antd'
import { foodDishes, foodTable, restaurants } from '../../Components/Constant'

const Home = () => {
    const [data, setData] = useState([])

    const getData = () => {
        const newData = foodTable.map((item) => {
            const dish = foodDishes.find((el) => el.id === item.foodId)
            const restaurant = restaurants.find((el) => el.id === item.restaurantId)

            return {
                ...item,
                foodName: dish ? dish.name : 'Unknown',
                foodCategory: dish ? dish.category : 'Unknown',
                foodRestaurant: restaurant ? restaurant.name : 'Unknown',
                ratings: item.ratings || 0, // Ensure ratings is available
                price: item.price || 0, // Ensure price is available
                url: item.url || '', // Ensure URL is available
            }
        })

        setData(newData)
    }

    useEffect(() => {
        getData()
    }, [])

    const CarouselImg = [
        {
            link: 'https://img.freepik.com/free-photo/pizza-pizza-filled-with-tomatoes-salami-olives_140725-1200.jpg?w=1060&t=st=1694236480~exp=1694237080~hmac=c705fc4be7db87ac2c812a716d1f64f1564a74aced25f3e1a3bb8e06211bf18c',
            name: 'Pizza',
        },
        {
            link: 'https://static.parentlane.com/contents/media/images/Kerala%20Parotha.jpg',
            name: 'Paratha',
        },
    ]

    return (
        <div className='container'>
            <div className='slider-container'>
                <h3 className='food-title-main'>Varieties of Food we can provide</h3>
                <Carousel autoplay>
                    {CarouselImg.map((item, index) => (
                        <div key={index}>
                            <div className='slider'>
                                <img className='slider-img' src={item.link} alt={item.name} />
                            </div>
                        </div>
                    ))}
                </Carousel>
            </div>
            <h3 className='food-title'>Best Food in the City</h3>
            <div className='card-container'>
                <Row gutter={[16, 16]}>
                    {data.map((item) => (
                        <Col key={item.id} xs={24} sm={12} xl={6} xxl={3}>
                            <Card
                                hoverable
                                cover={
                                    <img
                                        className='card-image'
                                        alt={item.foodName}
                                        src={item.url}
                                        style={{ width: '100%', height: '250px' }}
                                    />
                                }
                            >
                                <div className='flex-display-card'>
                                    <div
                                        style={{
                                            fontWeight: 'bold',
                                            overflow: 'hidden',
                                            textOverflow: 'ellipsis',
                                            whiteSpace: 'nowrap',
                                        }}
                                    >
                                        {item.foodName}
                                    </div>
                                    <div>₹{parseFloat(item.price).toFixed(2)}</div>
                                </div>
                                <div className='flex-display-card'>
                                    <div
                                        style={{
                                            overflow: 'hidden',
                                            textOverflow: 'ellipsis',
                                            whiteSpace: 'nowrap',
                                        }}
                                    >
                                        {item.foodRestaurant}
                                    </div>
                                    <div
                                        style={{
                                            borderRadius: '20%',
                                            padding: '0px 5px',
                                            color: 'white',
                                            backgroundColor:
                                                item.ratings >= 4 ? 'green' : item.ratings <= 2 ? 'red' : 'orange',
                                            height: 'fit-content',
                                        }}
                                    >
                                        {parseFloat(item.ratings).toFixed(1)}
                                    </div>
                                </div>
                                <div className='buttons'>
                                    <button className='button'>ADD TO CART</button>
                                    <button className='button'>FAVOURITES</button>
                                </div>
                            </Card>
                        </Col>
                    ))}
                </Row>
            </div>
        </div>
    )
}

export default Home
