                <fieldset style={{ border: '2px black solid', padding: '10px' }}>
                    <h2
                        style={{
                            textAlign: 'center',
                            fontSize: '40px',
                            color: 'black',
                        }}
                    >
                        <u>
                            <b> Sign Up </b>
                        </u>
                    </h2>
                    <p>
                        <label>Username:</label>
                        <input type='text' id='uname' autoComplete='off' placeholder='Please enter your name' />
                    </p>
                    <p>
                        <InputLabel htmlFor='standard-adornment-password'>Enter your Password</InputLabel>
                        <Input
                            type={values.showPassword ? 'text' : 'password'}
                            onChange={handlePasswordChange('password')}
                            value={values.password}
                            endAdornment={
                                <InputAdornment position='end'>
                                    <IconButton onClick={handleClickShowPassword} onMouseDown={handleMouseDownPassword}>
                                        {values.showPassword ? <Visibility /> : <VisibilityOff />}
                                    </IconButton>
                                </InputAdornment>
                            }
