import React, { useState } from 'react'
import { useNavigate } from 'react-router-dom'
import './Login.css'

function Login({ setLoggedInUser }) {
    const [input, setInput] = useState({
        username: '',
        password: '',
    })

    const [error, setError] = useState({
        username: false,
        password: false,
    })

    const [rememberMe, setRememberMe] = useState(false)

    const navigate = useNavigate()

    const handleInputChange = (e) => {
        const { name, value } = e.target
        setInput({
            ...input,
            [name]: value,
        })
    }

    const validateInput = () => {
        const newError = {
            username: !input.username,
            password: !input.password,
        }
        setError(newError)
        return !newError.username && !newError.password
    }

    const handleLogin = async () => {
        if (!validateInput()) return

        try {
            const response = await fetch('http://localhost:5000/existing', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify(input),
            })

            const data = await response.json()

            if (response.ok) {
                // Save the token, user, and rememberMe flag to localStorage
                localStorage.setItem('token', true)
                localStorage.setItem('user', JSON.stringify(input)) // Save user data
                localStorage.setItem('rememberMe', rememberMe)

                // Update the parent component's loggedInUser state
                setLoggedInUser(input.username)

                // Navigate to the home page after successful login
                navigate('/')
            } else {
                alert(`Login failed: ${data.message}`)
            }
        } catch (error) {
            console.error('Login error:', error)
        }
    }

    const handleCheckboxChange = () => {
        setRememberMe(!rememberMe)
    }

    return (
        <div className='login-container'>
            <h2>Login</h2>
            <div className='login-form'>
                <div className='input-group'>
                    <label htmlFor='username'>Username</label>
                    <input
                        type='text'
                        name='username'
                        value={input.username}
                        onChange={handleInputChange}
                        className={error.username ? 'input-error' : ''}
                        placeholder='Enter your username'
                    />
                    {error.username && <span className='error-message'>Username is required</span>}
                </div>
                <div className='input-group'>
                    <label htmlFor='password'>Password</label>
                    <input
                        type='password'
                        name='password'
                        value={input.password}
                        onChange={handleInputChange}
                        className={error.password ? 'input-error' : ''}
                        placeholder='Enter your password'
                    />
                    {error.password && <span className='error-message'>Password is required</span>}
                </div>
                <div className='input-group'>
                    <label>
                        <input type='checkbox' checked={rememberMe} onChange={handleCheckboxChange} />
                        Remember Me
                    </label>
                </div>
                <button onClick={handleLogin} className='login-button'>
                    Login
                </button>
            </div>
        </div>
    )
}

export default Login
