// Cart.js
import React, { useState } from 'react'

const Cart = ({ selectedItem }) => {
    const [count, setCount] = useState(1)
    const increaseCount = () => {
        setCount(count + 1)
    }
    const decreaseCount = () => {
        if (count === 1) {
            var isOne = true
        }
        if (isOne) {
            setCount(count)
        } else {
            setCount(count - 1)
        }
    }

    return (
        <div className='cart-container'>
            {/* Display the details of the selected food item */}
            <div className='order-display-container'>
                <div className='order-display'>
                    <img src={selectedItem.url} alt={selectedItem.foodName} className='display-img' />
                    <div className='display-head'>
                        <div>{selectedItem.foodName}</div>
                        <div className='small'>{selectedItem.foodRestaurant}</div>
                        <div className='line'></div>
                    </div>
                </div>
                <div className='order-content'>
                    <div
                        style={{
                            display: 'flex',
                            gap: '20px',
                            alignItems: 'center',
                        }}
                    >
                        <div className='icon'></div>
                        <div className='name-order'>{selectedItem.foodName}</div>
                        <div className='order-count'>
                            <button className='count-btn minus' onClick={decreaseCount}>
                                &#x2212;
                            </button>
                            <div className='count'>{count}</div>
                            <button className='count-btn plus' onClick={increaseCount}>
                                &#x2B;
                            </button>
                        </div>
                    </div>
                    <div className='price-order'>&#8377;{parseFloat(selectedItem.price).toFixed(2)}</div>
                </div>
                {/* You can continue displaying other details like bill, etc. */}
            </div>
        </div>
    )
}

export default Cart
