import React, { useEffect, useState } from 'react'
import { Card } from 'antd'
import { Carousel } from 'antd'
import { foodDishes, foodTable, restaurants } from '../../Components/Constant'

const Home = () => {
    const contentStyle = {
        height: '300px',
        lineHeight: '160px',
        textAlign: 'center',
    }

    const [data, setData] = useState([])

    const getData = () => {
        let newData = []
        foodTable.forEach((item) => {
            let newItem = { ...item }
            item.foodName = foodDishes.find((el) => el.id === item.foodId).name
            item.foodCategory = foodDishes.find((el) => el.id === item.foodId).category
            item.foodRestaurant = restaurants.find((el) => el.id === item.restaurantId).name
            newData.push(newItem)
        })
        setData(newData)
    }

    useEffect(() => {
        getData()
    }, [])

    return (
        <div className='container'>
            <Carousel autoplay>
                <div>
                    <h1 style={contentStyle}>One</h1>
                </div>
                <div>
                    <h2 style={contentStyle}>Two</h2>
                </div>
                <div>
                    <h3 style={contentStyle}>Three</h3>
                </div>
            </Carousel>
            <div className='food-title'>Best Food in the City</div>
            <div className='card-container'>
                {data.map((item, index) => {
                    return (
                        <Card
                            key={index}
                            hoverable
                            style={{
                                width: 'calc(25% - 25px)',
                                marginBottom: '20px',
                                marginRight: '25px',
                            }}
                            cover={<img alt='example' src={item.url} style={{ width: '100%', height: '250px' }} />}
                        >
                            <div className='flex-display-card'>
                                <div style={{ fontWeight: 'bold' }}>{item.foodName}</div>
                                <div>â‚¹{parseFloat(item.price).toFixed(2)}</div>
                            </div>
                            <div className='flex-display-card'>
                                <div
                                    style={{
                                        overflow: 'hidden',
                                        textOverflow: 'ellipsis',
                                        whiteSpace: 'nowrap',
                                    }}
                                >
                                    {item.foodRestaurant}
                                </div>
                                <div
                                    style={{
                                        borderRadius: '20%',
                                        padding: '0px 5px',
                                        color: 'white',
                                        backgroundColor:
                                            item.ratings >= 4 ? 'green' : item.ratings <= 2 ? 'red' : 'orange',
                                        height: 'fit-content',
                                    }}
                                >
                                    {parseFloat(item.ratings).toFixed(1)}
                                </div>
                            </div>
                        </Card>
                    )
                })}
            </div>
        </div>
    )
}

export default Home
