import React, { useState } from 'react'
import { Link } from 'react-router-dom'

const Login = () => {
    const [input, setInput] = useState({
        username: '',
        password: '',
    })

    const [error, setError] = useState({
        username: '',
        password: '',
    })

    const [showpsd, setShowpsd] = useState('password')

    const myFunction = (e) => {
        if (e.target.checked === true) {
            setShowpsd('text')
        } else {
            setShowpsd('password')
        }
    }
    const inputChange = (e) => {
        const { name, value } = e.target
        setInput((prev) => ({
            ...prev,
            [name]: value,
        }))
        validateInput(e)
    }

    const validateInput = (e) => {
        let { name, value } = e.target
        setError((prev) => {
            const stateObj = { ...prev, [name]: '' }

            switch (name) {
                case 'username':
                    if (!value) {
                        stateObj[name] = 'Please Enter Username.'
                    }
                    break
                case 'password':
                    if (!value) {
                        stateObj[name] = 'Please Enter Password.'
                    }
                    break
                default:
                    break
            }

            return stateObj
        })
    }
    return (
        <div className='login-card-container'>
            <div className='login-card-content'>
                <div className='login-card-title'>Login</div>
            </div>
            <p>
                <label>Username:</label>
                <input
                    type='text'
                    id='uname'
                    autoComplete='off'
                    placeholder='Username...'
                    name='username'
                    value={input.username}
                    onChange={inputChange}
                    onBlur={validateInput}
                />
                {error.username && (
                    <span className='err' style={{ color: 'red' }}>
                        {error.username}
                    </span>
                )}
            </p>
            <p>
                <label>Password:</label>
                <input
                    type={showpsd}
                    className='psd'
                    autoComplete='off'
                    placeholder='Password...'
                    value={input.password}
                    name='password'
                    onChange={inputChange}
                    onBlur={validateInput}
                />
                {error.password && (
                    <span className='err' style={{ color: 'red' }}>
                        {error.password}
                    </span>
                )}
            </p>
            <input type='checkbox' onChange={myFunction} />
            &nbsp; Show Password
            <>
                <div>
                    Don't want to create account? Explore our&nbsp;
                    <Link to='./' style={{ textDecoration: 'none', color: 'orange' }}>
                        Home
                    </Link>
                    <br />
                    Already have an account?&nbsp;
                    <Link to='./signup' style={{ textDecoration: 'none', color: 'red' }}>
                        Sign Up
                    </Link>
                </div>
            </>
            <button
                type='submit'
                id='submit'
                disabled={!input.username ? true : false || !input.password ? true : false}
            >
                Login
            </button>
            <button type='reset' id='reset'>
                Reset
            </button>
        </div>
    )
}

export default Login
