import React, { useState, useEffect } from 'react'
import { useNavigate, useLocation } from 'react-router-dom'
import Sidebar from './Sidebar'
import Name from '../Name'
import Order from './Order'
import Payments from './Payments'
import Address from './Address'
import Settings from './Settings'

import './Sidebar.css'

const Profile = () => {
    const navigate = useNavigate()
    const location = useLocation()

    const currentPage = location.pathname.split('/').pop()
    const [selectedPage, setSelectedPage] = useState(currentPage || 'orders') // Default to 'orders'

    // Update the URL when the selected page changes
    useEffect(() => {
        if (selectedPage !== currentPage) {
            navigate(`/profile/${selectedPage}`)
        }
    }, [selectedPage, currentPage, navigate])

    useEffect(() => {
        if (currentPage && currentPage !== selectedPage) {
            setSelectedPage(currentPage)
        }
    }, [currentPage])

    return (
        <div className='profile-container'>
            <Name />
            <div className='profile-body'>
                <Sidebar setSelectedPage={setSelectedPage} selectedPage={selectedPage} />
                <div className='main'>
                    {selectedPage === 'orders' ? (
                        <Order />
                    ) : selectedPage === 'payments' ? (
                        <Payments />
                    ) : selectedPage === 'addresses' ? (
                        <Address />
                    ) : selectedPage === 'settings' ? (
                        <Settings />
                    ) : (
                        <Order />
                    )}
                </div>
            </div>
        </div>
    )
}

export default Profile
