import React, { useState, useEffect } from 'react'
import { Provider } from 'react-redux'
import { Routes, Route, useNavigate } from 'react-router-dom'

import configureStore from './store/store'
import Signup from './Pages/Login/Signup'
import Home from './Pages/Home/Home'
import NotFound from './Pages/Usermenu/NotFound'
import Navbar from './Components/Navbar/Navbar'
import Login from './Pages/Login/Login'
import Profile from './Pages/Usermenu/Profile/Profile'
import Cart from './Pages/Usermenu/Profile/Cart'

import './Pages/Login/Login.css'
import './Components/Navbar/Navbar.css'
import './Pages/Home/Home.css'
import './Pages/Usermenu/Profile/Profile.css'
import './Pages/Usermenu/Profile/Order.css'
import './Pages/Usermenu/Name.css'
import './Pages/Usermenu/Profile/Cart.css'

const store = configureStore()

function App() {
    const [cartItems, setCartItems] = useState([])
    const navigate = useNavigate()

    // Retrieve username from localStorage
    const username = localStorage.getItem('username')

    // Fetch cart items when the component loads based on the logged-in user
    useEffect(() => {
        if (username) {
            const savedCartItems = localStorage.getItem(`cartItems_${username}`)
            if (savedCartItems) {
                setCartItems(JSON.parse(savedCartItems))
            }
        }
    }, [username])

    // Update the localStorage whenever cartItems change, storing it under the user's key
    useEffect(() => {
        if (cartItems.length > 0 && username) {
            localStorage.setItem(`cartItems_${username}`, JSON.stringify(cartItems))
        }
    }, [cartItems, username])

    const addToCart = (item) => {
        const newItem = { ...item, customId: Date.now() }

        const updatedCartItems = [...cartItems, newItem]
        setCartItems(updatedCartItems)
        localStorage.setItem(`cartItems_${username}`, JSON.stringify(updatedCartItems))
        alert('Item Added to Cart Successfully!!')
    }

    const removeFromCart = (itemToRemove) => {
        const updatedCart = cartItems.filter((item) => item.customId !== itemToRemove.customId)
        setCartItems(updatedCart)
        localStorage.setItem(`cartItems_${username}`, JSON.stringify(updatedCart))
    }

    const clearCart = () => {
        setCartItems([])
        localStorage.removeItem(`cartItems_${username}`)
    }

    const updateQuantity = (customId, newQuantity) => {
        setCartItems((prevItems) =>
            prevItems.map((item) => (item.customId === customId ? { ...item, quantity: newQuantity } : item))
        )
    }

    const HomeWrapper = () => <Home addToCart={addToCart} />
    const CartWrapper = () => (
        <Cart
            cartItems={cartItems}
            removeFromCart={removeFromCart}
            updateQuantity={updateQuantity}
            clearCart={clearCart}
        />
    )

    return (
        <Provider store={store}>
            <Navbar />
            <Routes>
                <Route path='/' element={<HomeWrapper />} />
                <Route path='/login' element={<Login />} />
                <Route path='/signup' element={<Signup />} />
                <Route path='/profile/:id' element={<Profile />} />
                <Route path='/cart' element={<CartWrapper />} />
                <Route path='*' element={<NotFound />} />
            </Routes>
        </Provider>
    )
}

export default App
