import React from 'react'
import { Carousel } from 'antd'

// Custom arrow component for previous arrow
const PrevArrow = (props) => {
    const { className, style, onClick } = props
    return (
        <div
            className={className}
            style={{ ...style, fontSize: '30px', zIndex: 1, left: '10px', color: '#000' }} // Customize the size, position, and color
            onClick={onClick}
        >
            ←
        </div>
    )
}

// Custom arrow component for next arrow
const NextArrow = (props) => {
    const { className, style, onClick } = props
    return (
        <div
            className={className}
            style={{ ...style, fontSize: '30px', zIndex: 1, right: '10px', color: '#000' }} // Customize the size, position, and color
            onClick={onClick}
        >
            →
        </div>
    )
}

const CustomCarousel = ({ data }) => {
    return (
        <Carousel
            autoplay
            arrows
            prevArrow={<PrevArrow />} // Pass custom previous arrow
            nextArrow={<NextArrow />} // Pass custom next arrow
        >
            {data.map((item, index) => (
                <div key={index}>
                    <div className='slider'>
                        <img className='slider-img' src={item.url} alt={item.name} />
                    </div>
                </div>
            ))}
        </Carousel>
    )
}

export default CustomCarousel
