import React, { useState, useEffect } from 'react'
import { Provider } from 'react-redux'
import { Routes, Route } from 'react-router-dom'

import configureStore from './store/store'
import Signup from './Pages/Login/Signup'
import Home from './Pages/Home/Home'
import NotFound from './Pages/Usermenu/NotFound'
import Navbar from './Components/Navbar/Navbar'
import Login from './Pages/Login/Login'
import Profile from './Pages/Usermenu/Profile/Profile'
import Cart from './Pages/Usermenu/Profile/Cart'

import './Pages/Login/Login.css'
import './Components/Navbar/Navbar.css'
import './Pages/Home/Home.css'
import './Pages/Usermenu/Profile/Profile.css'
import './Pages/Usermenu/Profile/Order.css'
import './Pages/Usermenu/Name.css'
import './Pages/Usermenu/Profile/Cart.css'

const store = configureStore()

function App() {
    const [cartItems, setCartItems] = useState([])
    const [loggedInUser, setLoggedInUser] = useState(null)

    // Load logged-in user from localStorage
    useEffect(() => {
        const user = JSON.parse(localStorage.getItem('user'))
        if (user) {
            setLoggedInUser(user.username) // or userID if you have it
        }
    }, [])

    // Load cart items for the logged-in user when the app loads
    useEffect(() => {
        if (loggedInUser) {
            const savedCartItems = localStorage.getItem(`cartItems_${loggedInUser}`)
            if (savedCartItems) {
                setCartItems(JSON.parse(savedCartItems))
            }
        }
    }, [loggedInUser])

    // Save cart items for the logged-in user whenever they are updated
    useEffect(() => {
        if (cartItems.length > 0 && loggedInUser) {
            localStorage.setItem(`cartItems_${loggedInUser}`, JSON.stringify(cartItems))
        }
    }, [cartItems, loggedInUser])

    const addToCart = (item) => {
        const newItem = { ...item, customId: Date.now() }
        const updatedCartItems = [...cartItems, newItem]
        setCartItems(updatedCartItems)
        if (loggedInUser) {
            localStorage.setItem(`cartItems_${loggedInUser}`, JSON.stringify(updatedCartItems)) // Update localStorage for the user
        }
        alert('Item Added to Cart Successfully!!')
    }

    const removeFromCart = (itemToRemove) => {
        const updatedCart = cartItems.filter((item) => item.customId !== itemToRemove.customId)
        setCartItems(updatedCart)
        if (loggedInUser) {
            localStorage.setItem(`cartItems_${loggedInUser}`, JSON.stringify(updatedCart)) // Update localStorage for the user
        }
    }

    const clearCart = () => {
        setCartItems([])
        if (loggedInUser) {
            localStorage.removeItem(`cartItems_${loggedInUser}`) // Clear the cart for the logged-in user
        }
    }

    const HomeWrapper = () => <Home addToCart={addToCart} />
    const CartWrapper = () => <Cart cartItems={cartItems} removeFromCart={removeFromCart} clearCart={clearCart} />

    return (
        <Provider store={store}>
            <Navbar />
            <Routes>
                <Route path='/' element={<HomeWrapper />} />
                <Route path='/login' element={<Login setLoggedInUser={setLoggedInUser} />} />
                <Route path='/signup' element={<Signup />} />
                <Route path='/profile/:id' element={<Profile />} />
                <Route path='/cart' element={<CartWrapper />} />
                <Route path='*' element={<NotFound />} />
            </Routes>
        </Provider>
    )
}

export default App
