import React, { useState } from 'react'
import { Link } from 'react-router-dom'
import '../Profile.css'

export default function Sidebar() {
    const handleClick = (event) => {
        setSelected(event)
    }
    const menu = [
        {
            url: '/profile/orders',
            name: 'Orders',
        },
    ]

    const [selected, setSelected] = useState(window.location.pathname())
    return (
        <div className='sidebar'>
            {menu.map((item) => {
                return (
                    <Link
                        to={item.url}
                        className={`sidebar-item ${selected === item.url ? 'bg-color' : ''}`}
                        onClick={() => handleClick(item.url)}
                    >
                        Orders
                    </Link>
                )
            })}

            <Link to='/profile/favourites' className='sidebar-item'>
                Favourites
            </Link>
            <Link to='/profile/payments' className='sidebar-item'>
                Payments
            </Link>
            <Link to='/profile/addresses' className='sidebar-item' onClick={handleClick}>
                Addresses
            </Link>
        </div>
    )
}
