import React, { useState, useRef, useEffect } from 'react'
import './Edit.css'
import { CloseOutlined } from '@ant-design/icons'

const Edit = ({ onClose }) => {
    const sidebarRef = useRef(null)

    useEffect(() => {
        // Add event listener to the entire document to handle clicks outside the sidebar
        document.addEventListener('mousedown', handleClickOutside)

        return () => {
            // Clean up the event listener when the component unmounts
            document.removeEventListener('mousedown', handleClickOutside)
        }
    }, [])

    const handleClickOutside = (event) => {
        if (sidebarRef.current && !sidebarRef.current.contains(event.target)) {
            // Click occurred outside the sidebar, so close it
            onClose()
        }
    }

    return (
        <div ref={sidebarRef} className='side-edit-container'>
            <div className='overlay' onClick={onClose}></div>
            <div className='side-edit-heading'>
                <div className='side-edit-head'>Edit Profile</div>
                <div className='closebtn' onClick={onClose}>
                    <CloseOutlined />
                </div>
            </div>
            <div className='side-edit-main'>
                <h5>Phone Number</h5>
                <div className='side-edit-inputcontainer'>
                    <div>1234567890</div>
                    <button>CHANGE</button>
                </div>
            </div>
            <hr />
            <div className='side-edit-main'>
                <h5>Email id</h5>
                <div className='side-edit-inputcontainer'>
                    <div>abc@gmail.com</div>
                    <button>CHANGE</button>
                </div>
            </div>
        </div>
    )
}

export default Edit
