import React, { useState, useEffect } from 'react'
import './AddAddress.css'

const AddAddress = () => {
    const [cities, setCities] = useState([])

    useEffect(() => {
        getCities()
    }, [])

    const getCities = async () => {
        try {
            const response = await fetch('http://localhost:5000/api/cities')
            const cityData = await response.json()
            setCities(cityData)
        } catch (error) {
            console.log('Error fetching cities: ', error)
        }
    }

    return (
        <div className='address-form-container'>
            <div className='address-form-subcontainer'>
                <h2 className='address-form-title'>ADD NEW ADDRESS</h2>
                <div className='address-radiobutton-container'>
                    <h4>Type of Address : </h4>
                    <div>
                        <input type='radio' name='AddressType' className='address-home' />
                        <label htmlFor='Home'>Home</label>
                    </div>
                    <div>
                        <input type='radio' name='AddressType' className='address-work' />
                        <label htmlFor='Work'>Work</label>
                    </div>
                </div>
                <input
                    type='text'
                    className='address-form-heading'
                    placeholder="Name of Address (Example: XYZ 's Home)..."
                />
                <textarea name='address' className='address-input' placeholder='Your Full Address...'></textarea>

                <div className='address-type-choice'>
                    <select name='city' className='address-city'>
                        <option value='' disabled selected>
                            Select a City
                        </option>
                        {cities.map((city, index) => (
                            <option key={index} value={city.name}>
                                {city.name}
                            </option>
                        ))}
                    </select>
                </div>
                <button className='btn address-submit-btn'>Add Address</button>
            </div>
        </div>
    )
}
export default AddAddress
