import React, { useState, useEffect } from 'react'
import './AddAddress.css'

const AddAddress = () => {
    const [cities, setCities] = useState([])
    const [formData, setFormData] = useState({
        addressType: '',
        addressName: '',
        address: '',
        city: '',
    })

    useEffect(() => {
        getCities()
    }, [])

    const getCities = async () => {
        try {
            const response = await fetch('http://localhost:5000/api/cities')
            const cityData = await response.json()
            setCities(cityData)
        } catch (error) {
            console.log('Error fetching cities: ', error)
        }
    }

    const handleChange = (e) => {
        const { name, value } = e.target
        setFormData((prevData) => ({
            ...prevData,
            [name]: value,
        }))
    }

    const handleSubmit = async () => {
        const userId = localStorage.getItem('uniqueId') // Get userId from localStorage

        if (!userId) {
            alert('User not logged in')
            return
        }

        try {
            const response = await fetch('http://localhost:5000/add-address', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({
                    ...formData,
                    userId,
                }),
            })

            const data = await response.json()
            if (response.ok) {
                alert('Address added successfully')
                window.location.href = '/profile/addresses'
            } else {
                alert(data.error || 'Failed to add address')
            }
        } catch (error) {
            console.error('Error adding address:', error)
            alert('Failed to add address')
        }
    }

    const back = () => {
        window.location.href = '/profile/addresses'
    }

    return (
        <div className='address-form-container'>
            <div className='address-form-subcontainer'>
                <h2 className='address-form-title'>ADD NEW ADDRESS</h2>
                <div className='address-radiobutton-container'>
                    <h4>Type of Address:</h4>
                    <div>
                        <input
                            type='radio'
                            name='addressType'
                            value='Home'
                            checked={formData.addressType === 'Home'}
                            onChange={handleChange}
                        />
                        <label>Home</label>
                    </div>
                    <div>
                        <input
                            type='radio'
                            name='addressType'
                            value='Work'
                            checked={formData.addressType === 'Work'}
                            onChange={handleChange}
                        />
                        <label>Work</label>
                    </div>
                </div>
                <input
                    type='text'
                    name='addressName'
                    className='address-form-heading'
                    placeholder="Name of Address (Example: XYZ's Home)..."
                    value={formData.addressName}
                    onChange={handleChange}
                />
                <textarea
                    name='address'
                    className='address-input'
                    placeholder='Your Full Address...'
                    value={formData.address}
                    onChange={handleChange}
                ></textarea>
                <div className='address-type-choice'>
                    <select name='city' className='address-city' value={formData.city} onChange={handleChange}>
                        <option value='' disabled>
                            Select a City
                        </option>
                        {cities.map((city, index) => (
                            <option key={index} value={city.name}>
                                {city.name}
                            </option>
                        ))}
                    </select>
                </div>
                <div className='address-buttons-container'>
                    <button className='btn back' onClick={back}>
                        Back
                    </button>
                    <button className='btn address-submit-btn' onClick={handleSubmit}>
                        Add Address
                    </button>
                </div>
            </div>
        </div>
    )
}

export default AddAddress
