import React, { useEffect, useState } from 'react'
import { Card, Row, Col, Carousel } from 'antd'

const PrevArrow = (props) => {
    const { className, style, onClick } = props
    return (
        <div
            className={className}
            style={{ ...style, fontSize: '24px', zIndex: 1, left: '10px', color: '#000' }} // Custom styling
            onClick={onClick}
        ></div>
    )
}

const NextArrow = (props) => {
    const { className, style, onClick } = props
    return (
        <div
            className={className}
            style={{ ...style, fontSize: '24px', zIndex: 1, right: '10px', color: '#000' }} // Custom styling
            onClick={onClick}
        ></div>
    )
}

const Home = ({ addToCart }) => {
    const [data, setData] = useState([])
    const [foodDishes, setFoodDishes] = useState([])
    const [foodTables, setFoodTables] = useState([])
    const [restaurants, setRestaurants] = useState([])

    const addItemToCart = (item) => {
        const cartItems = JSON.parse(localStorage.getItem('cartItems')) || []
        const updatedCartItems = [...cartItems, item]
        localStorage.setItem('cartItems', JSON.stringify(updatedCartItems))

        console.log('Updated Cart Items:', updatedCartItems) // Debugging to check if localStorage is updated
        addToCart(item) // Optionally handle cart state in a higher component
    }

    // Fetch all data from the backend API
    const getData = async () => {
        try {
            // Fetch food dishes
            const foodDishesResponse = await fetch('http://localhost:5000/api/fooddishes')
            const foodDishesData = await foodDishesResponse.json()
            setFoodDishes(foodDishesData)

            // Fetch food tables
            const foodTablesResponse = await fetch('http://localhost:5000/api/foodtables')
            const foodTablesData = await foodTablesResponse.json()
            setFoodTables(foodTablesData)

            // Fetch restaurants
            const restaurantsResponse = await fetch('http://localhost:5000/api/restaurants')
            const restaurantsData = await restaurantsResponse.json()
            setRestaurants(restaurantsData)

            // Merge the data
            const newData = foodTablesData.map((item) => {
                const dish = foodDishesData.find((dish) => dish.customId === item.foodId)
                const restaurant = restaurantsData.find((restaurant) => restaurant.customId === item.restaurantId)

                return {
                    ...item,
                    foodName: dish ? dish.name : 'Unknown',
                    foodCategory: dish ? dish.category : 'Unknown',
                    foodRestaurant: restaurant ? restaurant.name : 'Unknown',
                }
            })

            setData(newData)
        } catch (error) {
            console.error('Error fetching data:', error)
        }
    }

    useEffect(() => {
        getData()
    }, [])

    return (
        <div className='home-container'>
            <div className='slider-main-container'>
                <h3 className='food-title food-main-title'>Varieties of Food We Can Provide</h3>
                <div className='slider-main-container'>
                    <Carousel
                        className='custom-carousel'
                        arrows
                        autoplay
                        draggable={true}
                        prevArrow={<PrevArrow />}
                        nextArrow={<NextArrow />}
                    >
                        {data.map((item, index) => (
                            <div key={index}>
                                <div className='slider'>
                                    <img className='slider-img' src={item.url} alt={item.name} />
                                </div>
                            </div>
                        ))}
                    </Carousel>
                </div>
            </div>
            <h3 className='food-title'>Best Food in the City</h3>
            <div className='card-container'>
                <Row gutter={[16, 16]}>
                    {data.map((item, index) => (
                        <Col key={index} xs={24} sm={12} xl={6} xxl={3}>
                            <Card
                                hoverable
                                cover={
                                    <img
                                        className='card-image'
                                        alt='example'
                                        src={item.url}
                                        style={{ width: '100%', height: '250px' }}
                                    />
                                }
                            >
                                <div className='flex-display-card'>
                                    <div
                                        style={{
                                            fontWeight: 'bold',
                                            overflow: 'hidden',
                                            textOverflow: 'ellipsis',
                                            whiteSpace: 'nowrap',
                                        }}
                                    >
                                        {item.foodName}
                                    </div>
                                    <div>â‚¹{parseFloat(item.price).toFixed(2)}</div>
                                </div>
                                <div className='flex-display-card'>
                                    <div
                                        style={{
                                            overflow: 'hidden',
                                            textOverflow: 'ellipsis',
                                            whiteSpace: 'nowrap',
                                        }}
                                    >
                                        {item.foodRestaurant}
                                    </div>
                                    <div
                                        style={{
                                            borderRadius: '20%',
                                            padding: '0px 5px',
                                            color: 'white',
                                            backgroundColor:
                                                item.ratings >= 4 ? 'green' : item.ratings <= 2 ? 'red' : 'orange',
                                            height: 'fit-content',
                                        }}
                                    >
                                        {parseFloat(item.ratings).toFixed(1)}
                                    </div>
                                </div>
                                <div className='buttons'>
                                    <button
                                        className='button'
                                        type='button'
                                        onClick={(e) => {
                                            e.preventDefault()
                                            addItemToCart(item)
                                        }}
                                    >
                                        ADD TO CART
                                    </button>
                                    <button className='button' type='button' onClick={(e) => e.preventDefault()}>
                                        FAVOURITES
                                    </button>
                                </div>
                            </Card>
                        </Col>
                    ))}
                </Row>
            </div>
        </div>
    )
}

export default Home
