import React, { useState } from 'react'
import { Link } from 'react-router-dom'

//antd

const Login = () => {
    const [input, setInput] = useState({
        username: '',
        password: '',
    })

    const [error, setError] = useState({
        username: '',
        password: '',
    })

    const [showpsd, setShowpsd] = useState('password')

    const myFunction = (e) => {
        if (e.target.checked === true) {
            setShowpsd('text')
        } else {
            setShowpsd('password')
        }
    }
    const inputChange = (e) => {
        const { name, value } = e.target
        setInput((prev) => ({
            ...prev,
            [name]: value,
        }))
        validateInput(e)
    }

    const validateInput = (e) => {
        let { name, value } = e.target
        setError((prev) => {
            const stateObj = { ...prev, [name]: '' }

            switch (name) {
                case 'username':
                    if (!value) {
                        stateObj[name] = 'Please Enter Username.'
                    }
                    break
                case 'password':
                    if (!value) {
                        stateObj[name] = 'Please Enter Password.'
                    } else if (input.confirmPassword && value !== input.password) {
                        stateObj['confirmPassword'] = 'Password and Confirm Password does not match.'
                    } else {
                        stateObj['confirmPassword'] = input.confirmPassword ? '' : error.confirmPassword
                    }
                    break

                case 'confirmPassword':
                    if (!value) {
                        stateObj[name] = 'Please Confirm your Password.'
                    } else if (input.password && value !== input.password) {
                        stateObj[name] = 'Password and Confirm Password does not match.'
                    }
                    break
                default:
                    break
            }
        })
    }
    return (
        <>
            <form className='form'>
                <fieldset style={{ border: '2px black solid', padding: '10px', width: '600px' }}>
                    <h2
                        style={{
                            textAlign: 'center',
                            fontSize: '40px',
                            color: 'black',
                        }}
                    >
                        <u>
                            <b> Login </b>
                        </u>
                    </h2>
                    <p>
                        <label>Username:</label>
                        <input
                            type='text'
                            id='uname'
                            autoComplete='off'
                            placeholder='Username.'
                            name='username'
                            onChange={inputChange}
                            onBlur={validateInput}
                        />
                        {error.password && <span className='err'>{error.password}</span>}
                    </p>
                    <p>
                        <label>Password:</label>
                        <input
                            type={showpsd}
                            className='psd'
                            autoComplete='off'
                            placeholder='Password.'
                            onkeyup='passCheck(this.value)'
                            name='password'
                            onChange={inputChange}
                            onBlur={validateInput}
                        />
                    </p>
                    <input type='checkbox' onChange={myFunction} />
                    &nbsp; Show Password
                    <p>
                        <div>
                            Don't want to create account? Explore our&nbsp;
                            <Link to='./' style={{ textDecoration: 'none', color: 'orange' }}>
                                Home
                            </Link>
                            <br />
                            Already have an account?&nbsp;
                            <Link to='./signup' style={{ textDecoration: 'none', color: 'red' }}>
                                Sign Up
                            </Link>
                        </div>
                    </p>
                    <button type='submit' id='submit'>
                        Login
                    </button>
                    <button type='reset' id='reset'>
                        Reset
                    </button>
                </fieldset>
            </form>
        </>
    )
}

export default Login
