import { Input } from 'antd'
import React, { useState } from 'react'
import { Link } from 'react-router-dom'

const Login = () => {
    const [input, setInput] = useState({
        username: '',
        password: '',
    })

    const [error, setError] = useState({
        username: '',
        password: '',
    })

    const [showpsd, setShowpsd] = useState('password')

    const myFunction = (e) => {
        if (e.target.checked === true) {
            setShowpsd('text')
        } else {
            setShowpsd('password')
        }
    }
    const inputChange = (e) => {
        const { name, value } = e.target
        setInput({
            ...input,
            [name]: value,
        })
        validateInput(e)
    }

    const validateInput = (e) => {
        let { name, value } = e.target
        setError({ ...error, [name]: value ? '' : 'Required field' })
    }

    return (
        <div className='login-card-container'>
            <div className='login-card-content'>
                <div className='login-card-title'>Login</div>
                <div className='login-card-field'>
                    <label>Username:</label>
                    <Input
                        status={error.username ? 'error' : undefined}
                        placeholder='Username'
                        value={input.username}
                        onChange={inputChange}
                        name='username'
                        onBlur={validateInput}
                    />
                </div>
                <div className='login-card-field'>
                    <label>Password:</label>
                    <Input.Password
                        name='password'
                        status={error.password ? 'error' : undefined}
                        placeholder='Password'
                        value={input.password}
                        onChange={inputChange}
                        onBlur={validateInput}
                    />
                </div>
            </div>
            {/*  
            <input type='checkbox' onChange={myFunction} />
            &nbsp; Show Password
            <>
                <div>
                    Don't want to create account? Explore our&nbsp;
                    <Link to='./' style={{ textDecoration: 'none', color: 'orange' }}>
                        Home
                    </Link>
                    <br />
                    Already have an account?&nbsp;
                    <Link to='./signup' style={{ textDecoration: 'none', color: 'red' }}>
                        Sign Up
                    </Link>
                </div>
            </>
            <button
                type='submit'
                id='submit'
                disabled={!input.username ? true : false || !input.password ? true : false}
            >
                Login
            </button>
            <button type='reset' id='reset'>
                Reset
            </button> */}
        </div>
    )
}

export default Login
