import { Button, Checkbox } from 'antd'
import React, { useState, useEffect } from 'react'
import { Link, useHistory } from 'react-router-dom'
import InputField from '../../Components/InputField'

export const userArray2 = []

const Signup = () => {
    const history = useHistory()
    const [input, setInput] = useState({
        username: '',
        password: '',
        confirmPassword: '',
    })

    const [error, setError] = useState({
        username: '',
        password: '',
        confirmPassword: '',
    })
    const [rememberMe, setRememberMe] = useState(true)

    const myFunction = (e) => {
        setRememberMe(e.target.checked)
    }

    const inputChange = (e) => {
        const { name, value } = e.target
        setInput({
            ...input,
            [name]: value,
        })
        validateInput(e)
    }

    const validateInput = (e) => {
        let { name, value } = e.target
        setError({ ...error, [name]: value ? '' : 'Required field' })
        if (value && name.toLowerCase().includes('password') && input.password && input.confirmPassword) {
            setError({
                ...error,
                [name]: input.password !== input.confirmPassword ? 'Password and Confirm Password does not match.' : '',
            })
        }
    }

    useEffect(() => {
        let isAuth = localStorage.getItem('token') && JSON.parse(localStorage.getItem('token'))
        if (isAuth) {
            history.push('/')
        }
    }, [history])

    const handleSignup = async () => {
        try {
            const response = await fetch('http://localhost:5000/newuser', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify(input),
            })
            const data = await response.json()
            if (response.ok) {
                // Signup successful
                console.log('Signup successful:', data.message)
                // Redirect to home page or perform any other action
                history.push('/')
            } else {
                // Signup failed
                console.error('Signup failed:', data.message)
                // Handle error (e.g., display error message)
            }
        } catch (error) {
            console.error('Signup error:', error)
            // Handle error (e.g., display error message)
        }
    }

    return (
        <div className='login-card-container'>
            <div className='login-card-content'>
                <div className='login-card-title'>Sign Up</div>
                <div className='login-card-field'>
                    <InputField
                        name='username'
                        value={input.username}
                        label='Username'
                        error={error.username}
                        placeholder='Username...'
                        onChange={inputChange}
                        onBlur={validateInput}
                    />
                </div>
                <div className='login-card-field'>
                    <InputField
                        name='password'
                        value={input.password}
                        label='Password'
                        error={error.password}
                        placeholder='Password...'
                        onChange={inputChange}
                        onBlur={validateInput}
                        type='password'
                    />
                </div>

                <div className='login-card-field'>
                    <Checkbox checked={rememberMe} onChange={myFunction}>
                        Remember Me
                    </Checkbox>
                </div>
                <div className='login-card-field'>
                    <Button
                        style={{ width: '100%' }}
                        onClick={() => {
                            if (error.username || error.password || error.confirmPassword) return
                            localStorage.setItem('token', true)
                            localStorage.setItem('user', JSON.stringify(input))
                            localStorage.setItem('rememberMe', rememberMe)
                            history.push('/')
                            if (userArray2.length !== 0) userArray2.pop()
                            userArray2.push(input.username.toString())
                        }}
                        type='primary'
                    >
                        Sign Up
                    </Button>
                </div>
                <div className='login-card-field'>
                    Want to explore ??&nbsp;
                    <Link to='/'>Home</Link>
                </div>
                <div className='login-card-field'>
                    Already have an account ??&nbsp;
                    <Link to='/login'>Login</Link>
                </div>
            </div>
        </div>
    )
}

export default Signup
