import React, { useEffect, useState } from 'react'
import { Card } from 'antd'
import { Carousel } from 'antd'
import { foodDishes, foodTable, restaurants } from '../../Components/Constant'

const Home = () => {
    const [data, setData] = useState([])

    const getData = () => {
        let newData = []
        foodTable.forEach((item) => {
            let newItem = { ...item }
            item.foodName = foodDishes.find((el) => el.id === item.foodId).name
            item.foodCategory = foodDishes.find((el) => el.id === item.foodId).category
            item.foodRestaurant = restaurants.find((el) => el.id === item.restaurantId).name
            newData.push(newItem)
        })
        setData(newData)
    }

    useEffect(() => {
        getData()
    }, [])

    return (
        <div className='container'>
            <div className='food-title'>Varieties of Food we can provide</div>
            <Carousel autoplay>
                <div>
                    <div className='slider'>
                        <div id='slider-pizza'>
                            <img
                                src='https://img.freepik.com/free-photo/pizza-pizza-filled-with-tomatoes-salami-olives_140725-1200.jpg?w=1060&t=st=1694236480~exp=1694237080~hmac=c705fc4be7db87ac2c812a716d1f64f1564a74aced25f3e1a3bb8e06211bf18c'
                                alt='Pizza'
                                style={{
                                    opacity: 0.8,
                                    border: '2px solid black',
                                }}
                            />
                        </div>
                    </div>
                </div>
                {/* <div>
                    <div className='slider'>
                        <div className='slider-italian'>
                            <img
                                src='https://img.freepik.com/free-photo/pizza-pizza-filled-with-tomatoes-salami-olives_140725-1200.jpg?w=1060&t=st=1694236480~exp=1694237080~hmac=c705fc4be7db87ac2c812a716d1f64f1564a74aced25f3e1a3bb8e06211bf18c'
                                alt='Pizza'
                                style={{
                                    opacity: 0.7,
                                }}
                            />
                        </div>
                    </div>
                </div>
                <div>
                    <div className='slider'>
                        <div className='slider-fastfood'>
                            <img
                                src='https://img.freepik.com/free-photo/pizza-pizza-filled-with-tomatoes-salami-olives_140725-1200.jpg?w=1060&t=st=1694236480~exp=1694237080~hmac=c705fc4be7db87ac2c812a716d1f64f1564a74aced25f3e1a3bb8e06211bf18c'
                                alt='Pizza'
                                style={{
                                    opacity: 0.7,
                                }}
                            />
                        </div>
                    </div>
                </div> */}
            </Carousel>
            <div className='food-title'>Best Food in the City</div>
            <div className='card-container'>
                {data.map((item, index) => {
                    return (
                        <Card
                            key={index}
                            hoverable
                            style={{
                                width: 'calc(25% - 25px)',
                                marginBottom: '20px',
                                marginRight: '25px',
                            }}
                            cover={<img alt='example' src={item.url} style={{ width: '100%', height: '250px' }} />}
                        >
                            <div className='flex-display-card'>
                                <div style={{ fontWeight: 'bold' }}>{item.foodName}</div>
                                <div>₹{parseFloat(item.price).toFixed(2)}</div>
                            </div>
                            <div className='flex-display-card'>
                                <div
                                    style={{
                                        overflow: 'hidden',
                                        textOverflow: 'ellipsis',
                                        whiteSpace: 'nowrap',
                                    }}
                                >
                                    {item.foodRestaurant}
                                </div>
                                <div
                                    style={{
                                        borderRadius: '20%',
                                        padding: '0px 5px',
                                        color: 'white',
                                        backgroundColor:
                                            item.ratings >= 4 ? 'green' : item.ratings <= 2 ? 'red' : 'orange',
                                        height: 'fit-content',
                                    }}
                                >
                                    {parseFloat(item.ratings).toFixed(1)}
                                </div>
                            </div>
                        </Card>
                    )
                })}
            </div>
        </div>
    )
}

export default Home
