import React, { useState, useEffect } from 'react'
import Edit from './Edit'

const Name = () => {
    const [isSidebarOpen, setIsSidebarOpen] = useState(false)
    const [username, setUsername] = useState('')
    const [userData, setUserData] = useState(null)

    const toggleSidebar = () => {
        setIsSidebarOpen(!isSidebarOpen)
    }

    useEffect(() => {
        // Fetch the username from local storage or state where you stored it after login
        const storedUsername = JSON.parse(localStorage.getItem('user')).username
        setUsername(storedUsername)
    }, [])

    useEffect(() => {
        // Fetch user data by username
        const fetchUserData = async () => {
            try {
                const response = await fetch(`http://localhost:5000/user/${username}`)
                const data = await response.json()
                if (response.ok) {
                    setUserData(data)
                } else {
                    console.error('Fetch user data failed:', data.message)
                }
            } catch (error) {
                console.error('Fetch user data error:', error)
            }
        }
        fetchUserData()
    }, [username])

    return (
        <div className={`the-container ${isSidebarOpen ? 'disable-the-container' : ''}`}>
            <div className={`edit-sidebar ${isSidebarOpen ? 'open' : ''}`}>
                <Edit onClose={toggleSidebar} />
            </div>
            <div className='name-container'>
                <div className='name'>
                    {userData ? (
                        <div className='username'>
                            <div>Username: {username}</div>
                            <div>Email: {userData.email}</div>
                            <div>Mobile: {userData.mobile}</div>
                        </div>
                    ) : (
                        <div>User data loading...</div>
                    )}
                </div>
                <div className='edit'>
                    <button className='edit-profile' onClick={toggleSidebar}>
                        EDIT PROFILE
                    </button>
                </div>
            </div>
        </div>
    )
}

export default Name
